#!/usr/bin/env python3
import re

def format_dna(dna):
    new_dna = dna.split('\n')
    str_dna = ','.join(new_dna)
    del_com = re.sub(r",", r"", str_dna)
    split = re.sub(r"(\w{60})", r"\1\n", del_com)
    return(split)


               
