nt_count = {}
dna = ''
unique = set(dna)

print('nt', unique)

for nt in unique:
    count = dna.count(nt)
    nt_count[nt] = count

print(nt_count)

nt_count = {}

for nt in dna:
    if nt in nt_count:
        nt_count[nt] += 1
    else:
        nt_count[nt] = 1
print(nt_count)

print(nt_count.get('G'))
print(nt_count.get('C'))
print(nt_count.values())

ntcount = 0
for count in nt_count.values():
    ntcount += count
print(ntcount)
GCcount = nt_count.get('G') + nt_count.get('C')
print(GCcount)
print(f"""GC content is {GCcount/ntcount :.2%}""")